//! Test suite for the Web and headless browsers.
// NOTE: Other tests are within the relevant source files

#![cfg(target_arch = "wasm32")]

use wasm_bindgen_test::*;

wasm_bindgen_test_configure!(run_in_browser);

use base64::decode;
use rust_image_worker::process_image;
use wasm_bindgen::JsValue;

#[macro_use]
extern crate serde_derive;

#[cfg(test)]
enum TestImage {
    Jpeg,
    Png,
}

#[cfg(test)]
impl TestImage {
    fn get_vec(&self) -> Vec<u8> {
        decode(match self {
            TestImage::Jpeg => "",
            TestImage::Png => "",
        }).unwrap()
    }
}

#[derive(Serialize)]
struct ProcessImageParams {
    bg: Vec<u8>,
    dx: f32,
    dy: f32,
    format: String,
    height: u32,
    mode: String,
    quality: u8,
    scale: f32,
    width: u32,
}

#[wasm_bindgen_test]
fn process_jpeg_image_in_browser() {
    let data = TestImage::Jpeg.get_vec();

    assert_eq!(data.len(), 6391);

    let bytes = &data[..data.len()];

    process_image(
        &bytes,
        JsValue::from_serde(&ProcessImageParams {
            bg: vec![],
            dx: 0.0,
            dy: 0.0,
            format: "jpeg".to_string(),
            height: 100,
            mode: "fill".to_string(),
            quality: 90,
            scale: 1.0,
            width: 50,
        })
        .unwrap(),
    )
    .unwrap();
}

#[wasm_bindgen_test]
fn process_png_image_in_browser() {
    let data = TestImage::Png.get_vec();

    assert_eq!(data.len(), 2244);

    let bytes = &data[..data.len()];

    process_image(
        &bytes,
        JsValue::from_serde(&ProcessImageParams {
            bg: vec![],
            dx: 0.0,
            dy: 0.0,
            format: "png".to_string(),
            height: 100,
            mode: "fill".to_string(),
            quality: 90,
            scale: 1.0,
            width: 50,
        })
        .unwrap(),
    )
    .unwrap();
}